contracts/contracts/DCURewardManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./interfaces/IDCUToken.sol";

interface ISubmissionHypercerts {
    function userHypercertCount(address user) external view returns (uint256);
}

/**
 * @title DCURewardManager
 * @dev Handles reward accrual and distribution for the DeCleanup Network
 */
contract DCURewardManager is Ownable, ReentrancyGuard {
    enum RewardSource {
        ImpactClaim,
        Streak,
        Referral,
        ImpactReport,
        Verifier,
        Hypercert,
        Submission
    }

    struct UserRewardStats {
        uint256 currentBalance;
        uint256 totalEarned;
        uint256 totalClaimed;
        uint256 claimRewardsAmount;
        uint256 streakRewardsAmount;
        uint256 referralRewardsAmount;
        uint256 impactReportRewardsAmount;
    }

    IDCUToken public immutable dcuToken;
    address public nftCollection;
    address public submissionContract;
    address public treasury;

    uint256 public impactProductClaimReward = 10 ether;
    uint256 public referralReward = 3 ether; // standardized per Issue #5
    uint256 public streakReward = 3 ether;
    uint256 public impactReportReward = 5 ether;
    uint256 public verifierReward = 1 ether;
    uint256 public hypercertBonus = 10 ether;

    uint256 private constant MAX_REWARD_AMOUNT = 1000 ether;
    uint256 private constant MIN_LEVEL = 1;
    uint256 private constant MAX_LEVEL = 10;
    uint256 private constant STREAK_WINDOW = 7 days;

    mapping(address => uint256) public userBalances;
    mapping(address => uint256) public totalEarned;
    mapping(address => uint256) public totalClaimed;
    mapping(address => uint256) public claimRewardsAmount;
    mapping(address => uint256) public streakRewardsAmount;
    mapping(address => uint256) public referralRewardsAmount;
    mapping(address => uint256) public impactReportRewardsAmount;
    mapping(address => bool) public poiVerified;
    mapping(address => bool) public nftMinted;
    mapping(address => bool) private manualEligibility;
    mapping(address => bool) public rewardEligibility;
    mapping(address => uint256) public lastPoiTimestamp;
    mapping(address => mapping(uint256 => bool)) public impactProductClaimed;
    mapping(address => address) public referrers;
    mapping(address => bool) public referralRewarded;
    mapping(bytes32 => bool) public hypercertRewardsClaimed;

    event RewardAccrued(address indexed user, uint256 amount, uint8 rewardType, uint256 timestamp);
    event RewardsClaimed(address indexed user, uint256 amount, uint256 timestamp);
    event ReferralRegistered(address indexed invitee, address indexed referrer);
    event ReferralRewarded(address indexed referrer, address indexed invitee, uint256 amount);
    event PoiVerificationUpdated(address indexed user, bool verified);
    event NftMintStatusUpdated(address indexed user, bool minted);
    event RewardEligibilityUpdated(address indexed user, bool eligible, bool manualOverride);
    event NftCollectionUpdated(address indexed oldCollection, address indexed newCollection);
    event SubmissionContractUpdated(address indexed oldSubmission, address indexed newSubmission);
    event TreasuryUpdated(address indexed oldTreasury, address indexed newTreasury);
    event RewardAmountsUpdated(uint256 claimReward, uint256 referralReward, uint256 streakReward);
    event ReferralRewardUpdated(uint256 oldAmount, uint256 newAmount);
    event HypercertRewardClaimed(address indexed user, uint256 hypercertNumber, uint256 amount);
    event DCURewardImpactProduct(address indexed user, uint256 indexed level, uint256 amount);
    event DCURewardReferral(address indexed referrer, address indexed invitee, uint256 amount);
    event DCURewardStreak(address indexed user, uint256 amount, uint256 streakDays);

    modifier onlyNftOrOwner() {
        require(msg.sender == nftCollection || msg.sender == owner(), "REWARD__Unauthorized");
        _;
    }

    modifier onlySubmissionOrOwner() {
        require(
            msg.sender == submissionContract || msg.sender == owner(),
            "REWARD__Unauthorized"
        );
        _;
    }

    constructor(address _dcuToken, address _nftCollection) Ownable(msg.sender) {
        require(_dcuToken != address(0), "REWARD__InvalidAddress");
        dcuToken = IDCUToken(_dcuToken);
        nftCollection = _nftCollection;
        treasury = msg.sender;
    }

    // ----------- Configuration -----------

    function updateNftCollection(address _nftCollection) external onlyOwner {
        require(_nftCollection != address(0), "REWARD__InvalidAddress");
        address oldCollection = nftCollection;
        nftCollection = _nftCollection;
        emit NftCollectionUpdated(oldCollection, _nftCollection);
    }

    function setSubmissionContract(address _submission) external onlyOwner {
        require(_submission != address(0), "REWARD__InvalidAddress");
        address oldSubmission = submissionContract;
        submissionContract = _submission;
        emit SubmissionContractUpdated(oldSubmission, _submission);
    }

    function updateTreasury(address newTreasury) external onlyOwner {
        require(newTreasury != address(0), "REWARD__InvalidAddress");
        address oldTreasury = treasury;
        treasury = newTreasury;
        emit TreasuryUpdated(oldTreasury, newTreasury);
    }

    function updateRewardAmounts(
        uint256 newClaimReward,
        uint256 newReferralReward,
        uint256 newStreakReward
    ) external onlyOwner {
        _validateRewardAmount(newClaimReward);
        _validateRewardAmount(newReferralReward);
        _validateRewardAmount(newStreakReward);

        impactProductClaimReward = newClaimReward;
        referralReward = newReferralReward;
        streakReward = newStreakReward;
        emit RewardAmountsUpdated(newClaimReward, newReferralReward, newStreakReward);
    }

    /**
     * @dev Admin setter for referral reward with safety checks.
     */
    function updateReferralReward(uint256 newReferralReward) external onlyOwner {
        require(newReferralReward > 0, "REWARD__ZeroAmount");
        _validateRewardAmount(newReferralReward);
        uint256 old = referralReward;
        referralReward = newReferralReward;
        emit ReferralRewardUpdated(old, newReferralReward);
    }

    function setRewardEligibilityForTesting(address user, bool status) external onlyOwner {
        require(user != address(0), "REWARD__InvalidAddress");
        manualEligibility[user] = status;
        _updateEligibility(user);
    }

    // ----------- Verification & Mint Status -----------

    function setPoiVerificationStatus(address user, bool status) external onlyNftOrOwner {
        require(user != address(0), "REWARD__InvalidAddress");

        if (status) {
            uint256 previousTimestamp = lastPoiTimestamp[user];
            if (
                previousTimestamp != 0 &&
                block.timestamp - previousTimestamp <= STREAK_WINDOW
            ) {
                _addReward(user, streakReward, RewardSource.Streak);
                streakRewardsAmount[user] += streakReward;
                uint256 streakDays = (block.timestamp - previousTimestamp) / 1 days;
                if (streakDays == 0) {
                    streakDays = 1;
                }
                emit DCURewardStreak(user, streakReward, streakDays);
            }
            lastPoiTimestamp[user] = block.timestamp;
            poiVerified[user] = true;
        } else {
            poiVerified[user] = false;
            lastPoiTimestamp[user] = 0;
        }

        emit PoiVerificationUpdated(user, status);
        _updateEligibility(user);
    }

    function updateNftMintStatus(address user, bool hasMinted) external onlyNftOrOwner {
        require(user != address(0), "REWARD__InvalidAddress");
        nftMinted[user] = hasMinted;
        emit NftMintStatusUpdated(user, hasMinted);
        _updateEligibility(user);
    }

    function getVerificationStatus(address user)
        external
        view
        returns (bool poiStatus, bool nftStatus, bool eligible)
    {
        poiStatus = poiVerified[user];
        nftStatus = nftMinted[user];
        eligible = _isRewardEligible(user);
    }

    // ----------- Referral Logic -----------

    function registerReferral(address invitee, address referrer) external onlyOwner {
        require(invitee != address(0) && referrer != address(0), "REWARD__InvalidAddress");
        require(invitee != referrer, "REWARD__InvalidAddress");
        require(referrers[invitee] == address(0), "Referral already registered");

        referrers[invitee] = referrer;
        emit ReferralRegistered(invitee, referrer);
    }

    function getReferrer(address invitee) external view returns (address) {
        return referrers[invitee];
    }

    // ----------- Reward Accrual -----------

    function rewardImpactProductClaim(address user, uint256 level) external onlyNftOrOwner {
        _requireValidLevel(level);
        require(user != address(0), "REWARD__InvalidAddress");
        require(_isRewardEligible(user), "User not eligible for rewards");
        require(!impactProductClaimed[user][level], "Level already claimed");

        impactProductClaimed[user][level] = true;
        _addReward(user, impactProductClaimReward, RewardSource.ImpactClaim);
        claimRewardsAmount[user] += impactProductClaimReward;
        emit DCURewardImpactProduct(user, level, impactProductClaimReward);

        address referrer = referrers[user];
        if (referrer != address(0) && !referralRewarded[user]) {
            referralRewarded[user] = true;
            _addReward(referrer, referralReward, RewardSource.Referral);
            referralRewardsAmount[referrer] += referralReward;
            emit ReferralRewarded(referrer, user, referralReward);
            emit DCURewardReferral(referrer, user, referralReward);
        }
    }

    function rewardVerifier(address verifier) external onlySubmissionOrOwner {
        require(verifier != address(0), "REWARD__InvalidAddress");
        _addReward(verifier, verifierReward, RewardSource.Verifier);
    }

    function rewardImpactReports(address user, uint256 reportCount) external onlySubmissionOrOwner {
        require(user != address(0), "REWARD__InvalidAddress");
        require(reportCount > 0, "REWARD__ZeroAmount");
        uint256 rewardAmount = impactReportReward * reportCount;
        _addReward(user, rewardAmount, RewardSource.ImpactReport);
        impactReportRewardsAmount[user] += rewardAmount;
    }

    function rewardHypercertMint(address user) external onlyOwner {
        require(user != address(0), "REWARD__InvalidAddress");
        _addReward(user, hypercertBonus, RewardSource.Hypercert);
    }

    function claimHypercertReward(uint256 hypercertNumber) external nonReentrant {
        require(submissionContract != address(0), "Submission not set");
        require(hypercertNumber > 0, "Invalid hypercert number");

        uint256 mintedCount = ISubmissionHypercerts(submissionContract).userHypercertCount(
            msg.sender
        );
        require(mintedCount >= hypercertNumber, "Hypercert not minted");

        bytes32 claimKey = keccak256(abi.encodePacked(msg.sender, hypercertNumber));
        require(!hypercertRewardsClaimed[claimKey], "Reward already claimed");

        hypercertRewardsClaimed[claimKey] = true;
        _addReward(msg.sender, hypercertBonus, RewardSource.Hypercert);
        emit HypercertRewardClaimed(msg.sender, hypercertNumber, hypercertBonus);
    }

    function distributeRewards(address user, uint256 amount) external onlySubmissionOrOwner {
        _addReward(user, amount, RewardSource.Submission);
    }

    // ----------- Claiming -----------

    function claimRewards(uint256 amount) external nonReentrant {
        require(amount > 0, "REWARD__ZeroAmount");
        require(userBalances[msg.sender] >= amount, "REWARD__InsufficientBalance");

        userBalances[msg.sender] -= amount;
        totalClaimed[msg.sender] += amount;

        require(dcuToken.mint(msg.sender, amount), "Reward claim failed");
        emit RewardsClaimed(msg.sender, amount, block.timestamp);
    }

    // ----------- Views -----------

    function getBalance(address user) external view returns (uint256) {
        return userBalances[user];
    }

    function getTotalEarnedDCU(address user) external view returns (uint256) {
        return totalEarned[user];
    }

    function getRewardsBreakdown(address user)
        external
        view
        returns (
            uint256 claimReward,
            uint256 streakRewardAmount,
            uint256 referralRewardAmount,
        uint256 currentBalance,
        uint256 claimedRewards
        )
    {
        claimReward = claimRewardsAmount[user];
        streakRewardAmount = streakRewardsAmount[user];
        referralRewardAmount = referralRewardsAmount[user];
        currentBalance = userBalances[user];
        claimedRewards = totalClaimed[user];
    }

    function getUserRewardStats(address user) external view returns (UserRewardStats memory) {
        return
            UserRewardStats({
                currentBalance: userBalances[user],
                totalEarned: totalEarned[user],
                totalClaimed: totalClaimed[user],
                claimRewardsAmount: claimRewardsAmount[user],
                streakRewardsAmount: streakRewardsAmount[user],
                referralRewardsAmount: referralRewardsAmount[user],
                impactReportRewardsAmount: impactReportRewardsAmount[user]
            });
    }

    // ----------- Internal Helpers -----------

    function _addReward(
        address user,
        uint256 amount,
        RewardSource source
    ) internal {
        if (amount == 0) {
            return;
        }

        userBalances[user] += amount;
        totalEarned[user] += amount;

        emit RewardAccrued(user, amount, uint8(source), block.timestamp);
    }

    function _updateEligibility(address user) internal {
        bool eligible = _isRewardEligible(user);
        rewardEligibility[user] = eligible;
        emit RewardEligibilityUpdated(user, eligible, manualEligibility[user]);
    }

    function _isRewardEligible(address user) internal view returns (bool) {
        if (manualEligibility[user]) {
            return true;
        }
        return poiVerified[user] && nftMinted[user];
    }

    function _requireValidLevel(uint256 level) internal pure {
        require(level >= MIN_LEVEL && level <= MAX_LEVEL, "REWARD__InvalidLevel");
    }

    function _validateRewardAmount(uint256 amount) internal pure {
        require(amount <= MAX_REWARD_AMOUNT, "REWARD__ExcessiveRewardAmount");
    }
}

contracts/contracts/interfaces/IDCUToken.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

/**
 * @title IDCUToken
 * @dev Interface for DeCleanup Utility Token with dynamic supply model and governance features
 */
interface IDCUToken {
    // Basic ERC20 functions
    function name() external view returns (string memory);
    function symbol() external view returns (string memory);
    function decimals() external view returns (uint8);
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function transfer(address to, uint256 amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function transferFrom(address from, address to, uint256 amount) external returns (bool);

    // Permit extension functions
    function permit(address owner, address spender, uint256 value, uint256 deadline, uint8 v, bytes32 r, bytes32 s) external;
    function nonces(address owner) external view returns (uint256);
    function DOMAIN_SEPARATOR() external view returns (bytes32);

    // Minting and burning functions
    function mint(address to, uint256 amount) external returns (bool);
    function burn(uint256 amount) external;
    function burnFrom(address account, uint256 amount) external;

    // Admin functions
    function updateRewardLogicContract(address _newRewardLogicContract) external;
    function setSupplyCap(uint256 _supplyCap) external;
    function removeSupplyCap() external;

    // View functions
    function rewardLogicContract() external view returns (address);
    function totalMinted() external view returns (uint256);
    function supplyCap() external view returns (uint256);
    function supplyCapActive() external view returns (bool);
    function getTotalMinted() external view returns (uint256);
    function getCirculationStatus() external view returns (uint256 current, uint256 minted, bool capActive, uint256 cap);

    // Additional functions (for backward compatibility if needed)
    function burn(address from, uint256 amount) external;

    // Events
    event DCUMinted(address indexed to, uint256 amount, uint256 newBalance, uint256 timestamp);
    event DCUBurned(address indexed from, uint256 amount, uint256 newBalance, uint256 timestamp);
    event RewardLogicContractUpdated(address indexed oldRewardLogic, address indexed newRewardLogic, uint256 timestamp);
    event SupplyCapAdded(uint256 capAmount, uint256 timestamp, address indexed by);
    event SupplyCapRemoved(uint256 timestamp, address indexed by);

    // Events from ERC20
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
}

contracts/contracts/Submission.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "./interfaces/IDCUToken.sol";
import "./DCURewardManager.sol";

interface RecyclablesReward {
    function rewardRecyclables(address user, uint256 submissionId) external;
}

interface IImpactProductNFT {
    function verifyPOI(address user) external;
    function safeMint() external;
    function upgradeNFT(uint256 tokenId) external;
    function getUserNFTData(address user) external view returns (uint256 tokenId, uint256 impact, uint256 level);
    function _userHasMinted(address user) external view returns (bool);
    function verifiedPOI(address user) external view returns (bool);
}

/**
 * @title Submission
 * @dev Contract for handling form submissions from the DeCleanup dapp
 *
 * Notes on modifications:
 * - Introduced VERIFIER_ROLE and locked approve/reject to VERIFIER_ROLE (separate from ADMIN_ROLE).
 * - Fee transfer behavior is disabled by default (feeEnabled = false). Fee storage fields kept for ABI/compatibility.
 * - Hypercert eligibility now uses NFT level from ImpactProductNFT.getUserNFTData(...) as source of truth.
 * - Reward flow delegates to DCURewardManager (distributeRewards, rewardVerifier, rewardImpactReports).
 * - Minimal behavioral changes to storage layout to preserve compatibility with existing deployments/tests.
 */
contract Submission is Ownable, ReentrancyGuard, AccessControl {
    // Custom Errors
    error SUBMISSION__InvalidAddress();
    error SUBMISSION__InvalidSubmissionData();
    error SUBMISSION__SubmissionNotFound(uint256 submissionId);
    error SUBMISSION__Unauthorized(address user);
    error SUBMISSION__AlreadyApproved(uint256 submissionId);
    error SUBMISSION__AlreadyRejected(uint256 submissionId);
    error SUBMISSION__NoRewardsAvailable();
    error SUBMISSION__InsufficientSubmissionFee(uint256 sent, uint256 required);
    error SUBMISSION__RefundFailed();
    error SUBMISSION__CannotRefundApprovedSubmission(uint256 submissionId);

    // Role definitions for access control
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant VERIFIER_ROLE = keccak256("VERIFIER_ROLE");

    // Submission status enum
    enum SubmissionStatus { Pending, Approved, Rejected }

    // Submission structure
    struct CleanupSubmission {
        uint256 id;
        address submitter;
        string dataURI;
        string beforePhotoHash;
        string afterPhotoHash;
        string impactFormDataHash;
        int256 latitude;
        int256 longitude;
        uint256 timestamp;
        SubmissionStatus status;
        address approver;
        uint256 processedTimestamp;
        bool rewarded;
        uint256 feePaid;
        bool feeRefunded;
        bool hasImpactForm;
        bool hasRecyclables;
        string recyclablesPhotoHash;
        string recyclablesReceiptHash;
    }

    // Contract references
    IDCUToken public dcuToken;
    DCURewardManager public rewardManager;
    IImpactProductNFT public impactProductNFT;
    address public recyclablesRewardContract;

    // Storage
    mapping(uint256 => CleanupSubmission) public submissions;
    mapping(address => uint256[]) public userSubmissions;

    uint256 public submissionCount;
    uint256 public defaultRewardAmount;

    // Fee state (kept for compatibility). Disabled by default in this version.
    uint256 public submissionFee = 0; // initially 0
    bool public feeEnabled = false;   // disabled to avoid unexpected transfers during test/dev
    address payable public treasury = payable(address(0));

    uint256 public totalFeesCollected;
    uint256 public totalFeesRefunded;

    // Referrals & impact tracking
    mapping(address => address) public referrers; // invitee => referrer
    mapping(address => uint256) public userImpactFormCount; // Track impact forms per user

    // Hypercert tracking (legacy counters kept for history; NFT level is source of truth)
    mapping(address => uint256) public userCleanupCount;
    mapping(address => uint256) public userHypercertCount;

    // Events
    event SubmissionCreated(
        uint256 indexed submissionId,
        address indexed submitter,
        string dataURI,
        uint256 timestamp
    );

    event SubmissionApproved(
        uint256 indexed submissionId,
        address indexed approver,
        uint256 timestamp
    );

    event SubmissionRejected(
        uint256 indexed submissionId,
        address indexed approver,
        uint256 timestamp
    );

    event DefaultRewardUpdated(uint256 oldAmount, uint256 newAmount);

    event RewardAvailable(
        address indexed user,
        uint256 amount,
        uint256 submissionId,
        uint256 timestamp
    );

    event SubmissionFeeUpdated(uint256 newFee, bool enabled);
    event TreasuryUpdated(address indexed oldTreasury, address indexed newTreasury);
    event HypercertEligible(address indexed user, uint256 cleanupCount, uint256 hypercertNumber);
    event ReferralRegistered(address indexed referrer, address indexed invitee);
    event ImpactFormSubmitted(address indexed user, uint256 submissionId, uint256 totalForms);
    event RecyclablesSubmitted(address indexed user, uint256 indexed submissionId, string recyclablesPhotoHash, string recyclablesReceiptHash);
    event RecyclablesRewardContractUpdated(address indexed oldContract, address indexed newContract);

    /**
     * @dev Constructor sets up the contract with DCU token, DCURewardManager, and roles
     * @param _dcuToken Address of the DCU token contract
     * @param _rewardManager Address of the DCURewardManager contract
     * @param _defaultRewardAmount Default reward amount for approved submissions
     */
    constructor(address _dcuToken, address _rewardManager, uint256 _defaultRewardAmount) Ownable(msg.sender) {
        if (_dcuToken == address(0)) revert SUBMISSION__InvalidAddress();
        if (_rewardManager == address(0)) revert SUBMISSION__InvalidAddress();

        dcuToken = IDCUToken(_dcuToken);
        rewardManager = DCURewardManager(_rewardManager);
        defaultRewardAmount = _defaultRewardAmount;

        // Grant main admin roles to deployer/owner. Verifier should be granted explicitly with setup scripts.
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);

        // Keep fee disabled by default to avoid accidental transfers during development
        submissionFee = 0;
        feeEnabled = false;
        treasury = payable(address(0));
    }

    /**
     * @dev Create a new submission with IPFS hashes and optional referrer
     * @param dataURI IPFS URI or other storage reference to submission data
     * @param beforePhotoHash IPFS hash for before photo
     * @param afterPhotoHash IPFS hash for after photo
     * @param impactFormDataHash IPFS hash for impact form (empty string if not submitted)
     * @param lat Latitude (scaled by 1e6)
     * @param lng Longitude (scaled by 1e6)
     * @param referrer Address of referrer (address(0) if none)
     * @return submissionId The ID of the created submission
     */
    function createSubmission(
        string calldata dataURI,
        string calldata beforePhotoHash,
        string calldata afterPhotoHash,
        string calldata impactFormDataHash,
        int256 lat,
        int256 lng,
        address referrer
    ) external payable nonReentrant returns (uint256) {
        if (bytes(dataURI).length == 0) revert SUBMISSION__InvalidSubmissionData();
        if (bytes(beforePhotoHash).length == 0) revert SUBMISSION__InvalidSubmissionData();
        if (bytes(afterPhotoHash).length == 0) revert SUBMISSION__InvalidSubmissionData();

        // Fee validation kept but fee transfers are disabled by default.
        uint256 paid = 0;
        if (feeEnabled) {
            if (msg.value < submissionFee) revert SUBMISSION__InsufficientSubmissionFee(msg.value, submissionFee);
            paid = msg.value;
            totalFeesCollected += msg.value;
            // NOTE: We intentionally do NOT transfer to treasury here in this refactor.
            // Treasury sweeps (if desired) should be executed by an explicit administrative action.
        }

        // Register referrer on first submission
        if (userSubmissions[msg.sender].length == 0 && referrer != address(0) && referrer != msg.sender) {
            if (referrers[msg.sender] == address(0)) {
                referrers[msg.sender] = referrer;
                emit ReferralRegistered(referrer, msg.sender);
            }
        }

        uint256 submissionId = submissionCount;
        bool hasImpactForm = bytes(impactFormDataHash).length > 0;

        submissions[submissionId] = CleanupSubmission({
            id: submissionId,
            submitter: msg.sender,
            dataURI: dataURI,
            beforePhotoHash: beforePhotoHash,
            afterPhotoHash: afterPhotoHash,
            impactFormDataHash: impactFormDataHash,
            latitude: lat,
            longitude: lng,
            timestamp: block.timestamp,
            status: SubmissionStatus.Pending,
            approver: address(0),
            processedTimestamp: 0,
            rewarded: false,
            feePaid: paid,
            feeRefunded: false,
            hasImpactForm: hasImpactForm,
            hasRecyclables: false,
            recyclablesPhotoHash: "",
            recyclablesReceiptHash: ""
        });

        userSubmissions[msg.sender].push(submissionId);
        submissionCount++;

        // Track impact form submission
        if (hasImpactForm) {
            userImpactFormCount[msg.sender]++;
            emit ImpactFormSubmitted(msg.sender, submissionId, userImpactFormCount[msg.sender]);
        }

        emit SubmissionCreated(submissionId, msg.sender, dataURI, block.timestamp);

        return submissionId;
    }

    /**
     * @dev Attach recyclables data to an existing submission
     * Can only be called by the original submitter and only for pending submissions
     */
    function attachRecyclables(
        uint256 submissionId,
        string calldata recyclablesPhotoHash,
        string calldata recyclablesReceiptHash
    ) external nonReentrant {
        if (submissionId >= submissionCount) revert SUBMISSION__SubmissionNotFound(submissionId);

        CleanupSubmission storage s = submissions[submissionId];

        if (s.submitter != msg.sender) revert SUBMISSION__Unauthorized(msg.sender);
        if (s.status != SubmissionStatus.Pending) revert SUBMISSION__SubmissionNotFound(submissionId);
        if (bytes(recyclablesPhotoHash).length == 0) revert SUBMISSION__InvalidSubmissionData();

        s.hasRecyclables = true;
        s.recyclablesPhotoHash = recyclablesPhotoHash;
        s.recyclablesReceiptHash = recyclablesReceiptHash;

        emit RecyclablesSubmitted(msg.sender, submissionId, recyclablesPhotoHash, recyclablesReceiptHash);
    }

    /**
     * @dev Approve a submission (only for verifiers)
     * Delegates reward logic to DCURewardManager
     */
    function approveSubmission(uint256 submissionId) external nonReentrant onlyRole(VERIFIER_ROLE) {
        if (submissionId >= submissionCount) revert SUBMISSION__SubmissionNotFound(submissionId);

        CleanupSubmission storage s = submissions[submissionId];

        if (s.status == SubmissionStatus.Approved) revert SUBMISSION__AlreadyApproved(submissionId);
        if (s.status == SubmissionStatus.Rejected) revert SUBMISSION__AlreadyRejected(submissionId);

        s.status = SubmissionStatus.Approved;
        s.approver = msg.sender;
        s.processedTimestamp = block.timestamp;

        // Legacy counter preserved for compatibility (not used for eligibility)
        userCleanupCount[s.submitter]++;

        // Reward flow through unified manager
        if (!s.rewarded) {
            // Distribute rewards first, then mark as rewarded only if successful
            // This ensures the rewarded flag accurately reflects whether rewards were actually distributed
            try rewardManager.distributeRewards(s.submitter, defaultRewardAmount) {
                s.rewarded = true;
                emit RewardAvailable(s.submitter, defaultRewardAmount, submissionId, block.timestamp);
            } catch {
                // If reward distribution fails, don't set rewarded flag
                // This allows retry or manual distribution later
                emit RewardAvailable(s.submitter, 0, submissionId, block.timestamp);
            }
        }

        // Reward verifier (best-effort)
        try rewardManager.rewardVerifier(msg.sender) {
        } catch {
            // swallow - no revert on external reward failure
        }

        // Reward impact report (best-effort)
        if (s.hasImpactForm && userImpactFormCount[s.submitter] > 0) {
            try rewardManager.rewardImpactReports(s.submitter, 1) {
            } catch {
            }
        }

        // External recyclables reward (best-effort)
        if (s.hasRecyclables && recyclablesRewardContract != address(0)) {
            try RecyclablesReward(recyclablesRewardContract).rewardRecyclables(s.submitter, submissionId) {
            } catch {
            }
        }

        // Automatically verify POI when cleanup is approved (allows users to mint NFTs)
        // This makes the system work for new users without manual intervention
        if (address(impactProductNFT) != address(0)) {
            try impactProductNFT.verifyPOI(s.submitter) {
                // POI verified successfully
            } catch {
                // If POI verification fails (e.g., contract doesn't allow it), that's okay
                // User can still claim tokens, just won't be able to mint NFT until manually verified
            }
        }

        emit SubmissionApproved(submissionId, msg.sender, block.timestamp);

        // Emit HypercertEligible based on NFT level (ImpactProductNFT is source of truth)
        // Wrapped in try-catch because new users won't have an NFT until they claim their first level
        if (address(impactProductNFT) != address(0)) {
            try impactProductNFT.getUserNFTData(s.submitter) returns (uint256, uint256, uint256 level) {
                if (level > 0 && level % 10 == 0) {
                    userHypercertCount[s.submitter]++;
                    emit HypercertEligible(s.submitter, userCleanupCount[s.submitter], userHypercertCount[s.submitter]);
                }
            } catch {
                // If user has no NFT yet, that's fine - they'll get one when they claim
                // No need to emit hypercert eligibility for users without NFTs
            }
        }
    }

    /**
     * @dev Reject a submission (only for verifiers)
     */
    function rejectSubmission(uint256 submissionId) external nonReentrant onlyRole(VERIFIER_ROLE) {
        if (submissionId >= submissionCount) revert SUBMISSION__SubmissionNotFound(submissionId);

        CleanupSubmission storage s = submissions[submissionId];

        if (s.status == SubmissionStatus.Rejected) revert SUBMISSION__AlreadyRejected(submissionId);
        if (s.status == SubmissionStatus.Approved) revert SUBMISSION__AlreadyApproved(submissionId);

        s.status = SubmissionStatus.Rejected;
        s.approver = msg.sender;
        s.processedTimestamp = block.timestamp;

        emit SubmissionRejected(submissionId, msg.sender, block.timestamp);
    }

    /**
     * @dev Update the default reward amount (only for admins)
     */
    function updateDefaultReward(uint256 newRewardAmount) external onlyRole(ADMIN_ROLE) {
        uint256 oldAmount = defaultRewardAmount;
        defaultRewardAmount = newRewardAmount;
        emit DefaultRewardUpdated(oldAmount, newRewardAmount);
    }

    /**
     * @dev Update submission fee (only for admins). Fee transfers are disabled by default.
     */
    function updateSubmissionFee(uint256 newFee, bool enabled) external onlyRole(ADMIN_ROLE) {
        submissionFee = newFee;
        feeEnabled = enabled;
        emit SubmissionFeeUpdated(newFee, enabled);
    }

    /**
     * @dev Update treasury address (only for owner)
     */
    function updateTreasury(address payable newTreasury) external onlyOwner {
        if (newTreasury == address(0)) revert SUBMISSION__InvalidAddress();
        address oldTreasury = treasury;
        treasury = newTreasury;
        emit TreasuryUpdated(oldTreasury, newTreasury);
    }

    /**
     * @dev Update the RecyclablesReward contract address (only for owner)
     */
    function updateRecyclablesRewardContract(address _newRecyclablesRewardContract) external onlyOwner {
        address oldContract = recyclablesRewardContract;
        recyclablesRewardContract = _newRecyclablesRewardContract;
        emit RecyclablesRewardContractUpdated(oldContract, _newRecyclablesRewardContract);
    }

    /**
     * @dev Associate an ImpactProductNFT contract (only owner)
     */
    function setImpactProductNFT(address _impactProductNFT) external onlyOwner {
        if (_impactProductNFT == address(0)) revert SUBMISSION__InvalidAddress();
        impactProductNFT = IImpactProductNFT(_impactProductNFT);
    }

    /**
     * @dev Get all submissions for a user
     */
    function getSubmissionsByUser(address user) external view returns (uint256[] memory) {
        return userSubmissions[user];
    }

    /**
     * @dev Get the details of a submission
     */
    function getSubmissionDetails(uint256 submissionId) external view returns (CleanupSubmission memory) {
        if (submissionId >= submissionCount) revert SUBMISSION__SubmissionNotFound(submissionId);
        return submissions[submissionId];
    }

    /**
     * @dev Get Hypercert eligibility for a user
     * Uses ImpactProductNFT level as source of truth (level % 10 == 0 && level > 0)
     */
    function getHypercertEligibility(address user) external view returns (
        uint256 cleanupCount,
        uint256 hypercertCount,
        bool isEligible
    ) {
        cleanupCount = userCleanupCount[user];
        hypercertCount = userHypercertCount[user];
        if (address(impactProductNFT) == address(0)) {
            isEligible = false;
        } else {
            (, , uint256 level) = impactProductNFT.getUserNFTData(user);
            isEligible = (level > 0 && level % 10 == 0);
        }
    }

    /**
     * @dev Get a batch of submissions for pagination
     */
    function getSubmissionBatch(uint256 startIndex, uint256 batchSize) external view returns (CleanupSubmission[] memory) {
        uint256 endIndex = startIndex + batchSize;
        if (endIndex > submissionCount) {
            endIndex = submissionCount;
        }
        uint256 resultSize = endIndex - startIndex;
        CleanupSubmission[] memory result = new CleanupSubmission[](resultSize);
        for (uint256 i = 0; i < resultSize; i++) {
            result[i] = submissions[startIndex + i];
        }
        return result;
    }
}

contracts/contracts/tokens/ImpactProduct.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/utils/Base64.sol";

interface IDCUNftRewardManager {
    function updateNftMintStatus(address user, bool hasMinted) external;
    function rewardImpactProductClaim(address user, uint256 level) external;
    function setPoiVerificationStatus(address user, bool status) external;
}

/**
 * @title ImpactProductNFT
 * @dev Soulbound-style NFT that tracks POI verification, impact stats, and admin transfer overrides.
 */
contract ImpactProductNFT is ERC721, Ownable {
    using Strings for uint256;

    uint256 public constant MAX_LEVEL = 10;

    address public rewardsContract;
    address public submissionContract; // Submission contract can auto-verify POI
    uint256 private _tokenIdCounter;

    mapping(address => bool) public verifiedPOI;
    mapping(address => bool) public _userHasMinted;
    mapping(address => uint256) public userLevel;
    mapping(address => uint256) private _userTokenPointer;
    mapping(uint256 => uint256) public nftLevel;
    mapping(uint256 => uint256) public impactLevel;

    mapping(uint256 => bool) private _transferAuthorized;
    mapping(uint256 => address) private _authorizedRecipient;

    event POIVerified(address indexed user);
    event Minted(address indexed user, uint256 indexed tokenId);
    event NFTUpgraded(uint256 indexed tokenId, uint256 newLevel);
    event ImpactLevelUpdated(uint256 indexed tokenId, uint256 newImpactLevel);
    event RewardsContractUpdated(address indexed newRewardsContract);
    event TransferAuthorized(uint256 indexed tokenId, address indexed recipient);
    event TransferAuthorizationRevoked(uint256 indexed tokenId);
    event RewardDistributed(address indexed user, uint256 indexed level);

    constructor(address _rewardsContract) ERC721("Impact Product NFT", "IMPACT") Ownable(msg.sender) {
        require(_rewardsContract != address(0), "Invalid rewards contract address");
        rewardsContract = _rewardsContract;
    }

    // --- Verification ---

    /**
     * @dev Verify POI for a user
     * Can be called by owner or by Submission contract (for automatic verification)
     * This enables automatic POI verification when cleanups are approved, making the system
     * work for new users without manual intervention
     */
    function verifyPOI(address user) external {
        require(user != address(0), "Invalid address");

        // Allow owner to verify POI (manual verification)
        if (msg.sender == owner()) {
            verifiedPOI[user] = true;
            emit POIVerified(user);
            // Notify reward manager for streak reward distribution
            if (rewardsContract != address(0)) {
                try IDCUNftRewardManager(rewardsContract).setPoiVerificationStatus(user, true) {} catch {}
            }
            return;
        }

        // Allow Submission contract to verify POI automatically
        // This is set via setSubmissionContract() by the owner
        if (msg.sender == submissionContract && submissionContract != address(0)) {
            verifiedPOI[user] = true;
            emit POIVerified(user);
            // Notify reward manager for streak reward distribution
            if (rewardsContract != address(0)) {
                try IDCUNftRewardManager(rewardsContract).setPoiVerificationStatus(user, true) {} catch {}
            }
            return;
        }

        // If we get here, caller is not authorized
        revert("Only owner or Submission contract can verify POI");
    }

    // --- Configuration ---

    function setRewardsContract(address newRewardsContract) external onlyOwner {
        require(newRewardsContract != address(0), "Invalid rewards contract address");
        rewardsContract = newRewardsContract;
        emit RewardsContractUpdated(newRewardsContract);
    }

    function setSubmissionContract(address newSubmissionContract) external onlyOwner {
        require(newSubmissionContract != address(0), "Invalid submission contract address");
        submissionContract = newSubmissionContract;
    }

    // --- Minting ---

    function safeMint() external {
        _requireVerified(msg.sender);
        _mintTo(msg.sender);
    }

    function mint(address user) external onlyOwner {
        _requireVerified(user);
        _mintTo(user);
    }

    function _mintTo(address user) internal {
        require(!_userHasMinted[user], "You have already minted a token");

        uint256 tokenId = _tokenIdCounter;
        _tokenIdCounter += 1;

        nftLevel[tokenId] = 1;
        impactLevel[tokenId] = 1;
        userLevel[user] = 1;
        _userHasMinted[user] = true;

        _safeMint(user, tokenId);
        _setUserTokenPointer(user, tokenId);
        _notifyRewardsContract(user, true);

        // Distribute level reward (10 $cDCU) and referral reward (3 $cDCU) if applicable
        if (rewardsContract != address(0)) {
            try IDCUNftRewardManager(rewardsContract).rewardImpactProductClaim(user, 1) {} catch {}
        }

        emit Minted(user, tokenId);
    }

    // --- Upgrades & Impact ---

    function upgradeNFT(uint256 tokenId) external {
        _requireTokenOwned(tokenId);
        require(ownerOf(tokenId) == msg.sender, "You don't own this token");
        _requireVerified(msg.sender);

        uint256 currentLevel = nftLevel[tokenId];
        require(currentLevel < MAX_LEVEL, "You have reached the maximum level");

        uint256 newLevel = currentLevel + 1;
        nftLevel[tokenId] = newLevel;
        userLevel[msg.sender] = newLevel;

        // Distribute level reward (10 $cDCU) for the new level
        if (rewardsContract != address(0)) {
            try IDCUNftRewardManager(rewardsContract).rewardImpactProductClaim(msg.sender, newLevel) {} catch {}
        }

        emit NFTUpgraded(tokenId, newLevel);
    }

    function updateImpactLevel(uint256 tokenId, uint256 newImpactLevel) external onlyOwner {
        _requireTokenOwned(tokenId);
        require(
            newImpactLevel > 0 && newImpactLevel <= MAX_LEVEL,
            "Invalid impact level range"
        );

        impactLevel[tokenId] = newImpactLevel;
        emit ImpactLevelUpdated(tokenId, newImpactLevel);
    }

    // --- Reward Distribution ---

    function distributeReward(address user, uint256 level) external onlyOwner {
        require(user != address(0), "Invalid user address");
        require(verifiedPOI[user], "User is not a verified POI");
        require(level >= 1 && level <= MAX_LEVEL, "Invalid level range");

        emit RewardDistributed(user, level);
    }

    // --- Soulbound controls ---

    function authorizeTransfer(uint256 tokenId, address recipient) external onlyOwner {
        _requireTokenOwned(tokenId);
        require(recipient != address(0), "Invalid recipient");

        _transferAuthorized[tokenId] = true;
        _authorizedRecipient[tokenId] = recipient;
        emit TransferAuthorized(tokenId, recipient);
    }

    function revokeTransferAuthorization(uint256 tokenId) external onlyOwner {
        _requireTokenOwned(tokenId);
        _clearTransferAuthorization(tokenId);
        emit TransferAuthorizationRevoked(tokenId);
    }

    function adminTransfer(uint256 tokenId, address recipient) external onlyOwner {
        _requireTokenOwned(tokenId);
        require(recipient != address(0), "Invalid recipient");

        address from = ownerOf(tokenId);
        _transferAuthorized[tokenId] = true;
        _authorizedRecipient[tokenId] = recipient;
        _transfer(from, recipient, tokenId);
        _clearTransferAuthorization(tokenId);
    }

    function isTransferAuthorized(uint256 tokenId) external view returns (bool authorized, address recipient) {
        authorized = _transferAuthorized[tokenId];
        recipient = _authorizedRecipient[tokenId];
    }

    // --- Views ---

    function getUserNFTData(address user) external view returns (uint256 tokenId, uint256 impact, uint256 level) {
        uint256 pointer = _userTokenPointer[user];
        require(pointer != 0, "User has no NFT");
        tokenId = pointer - 1;
        require(ownerOf(tokenId) == user, "User has no NFT");

        impact = impactLevel[tokenId];
        level = userLevel[user];
    }

    function tokenURI(uint256 tokenId) public view override returns (string memory) {
        _requireTokenOwned(tokenId);

        uint256 level = nftLevel[tokenId];
        uint256 impact = impactLevel[tokenId];
        string memory category = _categoryForLevel(level);

        bytes memory metadata = abi.encodePacked(
            '{"name":"Impact Product #',
                tokenId.toString(),
            '","description":"DeCleanup Impact Product NFT","attributes":[',
            _attributeJson("Level", level.toString()),
            ",",
            _attributeJson("Impact", impact.toString()),
            ",",
            _attributeJson("Category", category),
            "]}"
        );

        return string.concat("data:application/json;base64,", Base64.encode(metadata));
    }

    // --- Internal helpers ---

    function _clearTransferAuthorization(uint256 tokenId) internal {
        if (_transferAuthorized[tokenId]) {
            _transferAuthorized[tokenId] = false;
            _authorizedRecipient[tokenId] = address(0);
        }
    }

    function _requireVerified(address user) internal view {
        require(verifiedPOI[user], "You are not a verified POI");
    }

    function _requireTokenOwned(uint256 tokenId) internal view {
        require(_ownerOf(tokenId) != address(0), "Token does not exist");
    }

    function _categoryForLevel(uint256 level) internal pure returns (string memory) {
        if (level <= 3) return "Newbie";
        if (level <= 6) return "Pro";
        if (level <= 9) return "Hero";
        return "Guardian";
    }

    function _attributeJson(string memory traitType, string memory value) internal pure returns (bytes memory) {
        return abi.encodePacked('{"trait_type":"', traitType, '","value":"', value, '"}');
    }

    function _setUserTokenPointer(address user, uint256 tokenId) internal {
        _userTokenPointer[user] = tokenId + 1;
    }

    function _notifyRewardsContract(address user, bool hasMinted) internal {
        if (rewardsContract != address(0)) {
            try IDCUNftRewardManager(rewardsContract).updateNftMintStatus(user, hasMinted) {} catch {}
        }
    }

    function _update(address to, uint256 tokenId, address auth) internal override returns (address) {
        address previousOwner = _ownerOf(tokenId);

        if (previousOwner != address(0) && to != address(0)) {
            if (!_transferAuthorized[tokenId] || _authorizedRecipient[tokenId] != to) {
                revert("ImpactProductNFT: transfers are restricted (soulbound NFT)");
            }
        }

        address from = super._update(to, tokenId, auth);

        if (previousOwner != address(0) && to != address(0)) {
            _clearTransferAuthorization(tokenId);
        }

        if (from != to) {
            if (from != address(0)) {
                _userTokenPointer[from] = 0;
            }
            if (to != address(0)) {
                _setUserTokenPointer(to, tokenId);
                _userHasMinted[to] = true;
                userLevel[to] = nftLevel[tokenId];
                _notifyRewardsContract(to, true);
    }
}
        return from;
    }
}

